import React, {useEffect, useState} from "react";

const Film = ({id}) => {
    const [data, setData] = useState(null)

    useEffect(() => {
        setData(null)

        fetch(` 8bd7ad481d1640e1826b9b2df2461c0f&language=en-US&page=${id}&include_adult=false&query=3`)
            .then(res => res.json())
            .then(
                (result) => setData(result),
                (e) => console.warn('fetch failure', e)
            )
    }, [id])

    return (
        <div className='post-wrapper'>
            {data ? (
                <div>
                    <h2>{data.title}</h2>
                </div>
            ) : 'Loading...'}
        </div>
    )
}


export default Film;
