import {Link} from "react-router-dom";
const IMG = 'https://image.tmdb.org/t/p/w300/'
 const AddFavourites = ({film}) =>{



useEffect(()=> {}, [])
return(

    <div className='image-container movie overlay'>
                 <img src={`https://image.tmdb.org/t/p/w500/${film?.poster_path}`} className='card-img' alt='...'/>

                 <div className='info'>
                     <h3>{film?.title}</h3>
                     <span>
               {film?.vote_average}
           </span>
                 </div>
                 <div className='over'>
                     <p>{film?.overview}</p>
                 </div>

             </div>
)
 }

 export default AddFavourites
