import React from "react";
import {Link} from "react-router-dom";
import AddFavourite from "../Add favourites/add";

const IMG_API = 'https://image.tmdb.org/t/p/w300/'


   

    const Movie = ({id, title, poster_path, overview, vote_average, props}) => (

        <div className='image-container movie overlay'>
            <img src={poster_path ? IMG_API + poster_path :
                "https://images.unsplash.com/photo-1609936968684-36b55c06ab7a?ixid=MXwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw3M3x8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60"}
                 alt='title'/>
            <div className='info'>
                <h3>{title}</h3>
                <span>
               {vote_average}
           </span>
            </div>
            <div className='over'>
                <p>{overview}</p>
                <Link to={`/movie/${id}`} className='link'>Details</Link>

            </div>

        </div>

    )

export default Movie
