import React, {useState} from "react";
import {Link, useParams} from "react-router-dom";
import {click} from "@testing-library/user-event/dist/click";

const IMG_API = 'https://image.tmdb.org/t/p/w300/'



   const Movie = ({movie}) => {
            this.keyName = 'films'
       getProducts =() => {
                const filmsLocalStorage = localStorage.getItem(this.keyName)
                if(filmsLocalStorage !== null){
                    return JSON.parse(filmsLocalStorage)
                }
                return []
            }
       const localStorageUtil = new LocalStorageUtil()

       const a = localStorageUtil.getProducts()
       console.log(a)

        return (

            <div className='image-container movie overlay'>
                <img src={movie?.poster_path ? IMG_API + movie.poster_path :
                    "https://images.unsplash.com/photo-1609936968684-36b55c06ab7a?ixid=MXwxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHw3M3x8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60"}
                     alt='title'/>
                <div className='info'>
                    <h3>{movie?.title}</h3>
                    <span>
               {movie?.vote_average}
           </span>
                </div>
                <div className='over'>
                    <p>{movie?.overview}</p>
                    <div className='bt'>
                    <Link to={`/movie/${movie?.id}`} className='link'>Details</Link>
                        <button className='link' type='button' onClick={()=> getProducts()}>Favourite</button>
                    </div>
                </div>

            </div>

        )
    }
export default Movie
