import React, {useEffect, useState} from "react";
import Header from "./components/Header/header";
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import MoviesDetail from "./components/MoviesDetail/detail";


const API_KEY = "8bd7ad481d1640e1826b9b2df2461c0f"

export default function App() {
    const MOVIE_API = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=en-US&page=1&include_adult=false&query=3`
    const SEARCH_API =  `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=/`
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    useEffect(() =>{
       getMovies(MOVIE_API) ;
    }, [])

    const getMovies = API => {
        fetch(API)
            .then(res => res.json())
            .then(data => {
                setMovies(data.results)
            })
    }
    const handleOnSubmit = event => {
        event.preventDefault();
        if (searchTerm) {
            getMovies(SEARCH_API + searchTerm);
            setSearchTerm("");
        }
    }
    const handleOnChange = event => {
        setSearchTerm(event.target.value)
    }
    return(
     <Router>
         <Header />
         <Switch>
             <Route exact path='/'>
                 <header>
                     <form onSubmit={handleOnSubmit}>
                         <input
                         className='search'
                         type='search'
                         placeholder='Search'
                         value={searchTerm}
                         onChange={handleOnChange}
                         />
                     </form>
                 </header>
             </Route>
             <Route path='/movie/:movieId'>
                 <MoviesDetail movies={movies} />
             </Route>
         </Switch>
     </Router>
    )
}
