import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";
import axios from "axios";


export default function MoviesDetail(props) {
    const {movies} = props;
    const [movie, setMovie] = useState();
    const params = useParams();
    console.log(params)

const getDetails = async () => {
        try{
            const res =  await axios.get(`https://api.themoviedb.org/3/movie/${params.movieId}?api_key=8bd7ad481d1640e1826b9b2df2461c0f&language=en-US`)
            // setMovie(res)
            console.log(res)
        }catch (e){
            console.error(e)
        }
}
    useEffect(() => {
        getDetails()
       //  setMovie(
       //      movies.filter(item => Number(item.id) === Number(params.movieId))
       // [0] )

    }, [])

    return (
      <div className='container'>
          <nav aria-label='breadcrumb' className='nav'>
              <ol className='breadcrumb'>
                  <li className='link'><Link to='/'>Home</Link></li>
              </ol>
          </nav>
          <div className='content'>
              <img src={`https://image.tmdb.org/t/p/w500/${movie?.poster_path}`} className='card-img' alt='...'/>
          <h1>{movie.title}</h1>
              <p>{movie.overview}</p>
              <div className='vote'>
              <h4>IMDB: {movie.vote_average} / 10</h4>
              <h4>Vote count: {movie.vote_count}</h4>
              </div>
              <div className='release'>
              <h4>Release: {movie.release_date}</h4>
              <h4>Popularity: {movie.popularity}</h4>
              </div>
          </div>
      </div>
    )
}
