import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import axios from "axios";

const AddFavourites = () => {
    const [movie, setMovie] = useState([])
    const params = useParams()

    const getFav = async () => {
        try{
            const res =  await axios.get(`https://api.themoviedb.org/3/movie/${params.movieId}?api_key=8bd7ad481d1640e1826b9b2df2461c0f&language=en-US`)
            setMovie(res.data)
            console.log(res)
        }catch (e){
            console.error(e)
        }
    }
    useEffect(() => {
        getFav()
        //  setMovie(
        //      movies.filter(item => Number(item.id) === Number(params.movieId))
        // [0] )

    }, [])
   return (
<div>
    <div className='image-container movie overlay'>
        <img src={movie?.poster_path }   alt='title'/>
        <div className='info'>
            <h3>{movie?.title}</h3>
            <span>
               {movie?.vote_average}
           </span>
        </div>
        <div className='over'>
            <p>{movie?.overview}</p>
        </div>
    </div>
</div>

   )


}
export default AddFavourites
