import React, {useEffect, useState} from "react";


function Pages() {
    const perPage = 20
    const [totalPages, setTotalPages] = useState(1)
    const [page, setPage] = useState(1)

    const [filmList, setFilmList] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(() =>{
        const getFilmList = () => {
            setLoading(true)
            fetch(`https://api.themoviedb.org/3/search/movie?api_key=8bd7ad481d1640e1826b9b2df2461c0f&language=en-US&page={ COUNT PAGE}&include_adult=false&query=3`)
        }
    })
}
