import React, {useEffect, useState} from "react";
import {BrowserRouter, Routes, Route, Redirect} from "react-router-dom";
import Header from "./components/Header/header";


const API_KEY = "8bd7ad481d1640e1826b9b2df2461c0f"



export default function App() {
    const [search, setSearch] = useState("");
    const [movies, setMovies] = useState([]);

    const PRO_API = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=en-US&page=1&include_adult=false&query=3`;
    const SEARCH_API = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=/`;

    useEffect(() =>{
        fetch(PRO_API)
            .then(response => response.json())
            .then(info => {
                setMovies(info.result)
            })
    },[])
const Clicker = event => {
        event.preventDefault();

        fetch(SEARCH_API + search)
        .then(rsp => rsp.json())
            .then(data => setMovies(data.results));
}
return (
    <BrowserRouter>
        <Header />

    </BrowserRouter>
)

}
