import React, {useEffect, useState} from "react";


function Pages() {
    const [totalPages, setTotalPages] = useState(1)
    const [page, setPage] = useState(1)

    const [filmList, setFilmList] = useState([])
    const [loading, setLoading] = useState(false)

    useEffect(() =>{
        const getFilmList = () => {
            setLoading(true)
            fetch(`https://api.themoviedb.org/3/search/movie?api_key=8bd7ad481d1640e1826b9b2df2461c0f&language=en-US&page=${page}&include_adult=false&query=3`)
                .then(res => res.json())
                .then(res => {
                    let data = JSON.parse(JSON.stringify(data))
                    setTotalPages(res.total_pages);
                    setFilmList([...filmList, ...data.data])
                    setLoading(false)
                })
        }
        getFilmList()
    },[page] )

    return (
        <div className='page-container'>
            {totalPages !== page && <button className="btn-load-more" onClick={() => setPage(page + 1)}>{loading ? 'Loading...' : 'Load More'}</button>}
        </div>
    )
}

export default Pages
