import React, {useState} from "react";
import {Link} from "react-router-dom";
const IMG = 'https://image.tmdb.org/t/p/w300/'
 const AddFavourites = () =>{
let keys = JSON.parse(localStorage.getItem('allEntries'))
     console.log(keys)
     for (let key of keys) {
         return (
             <div className='image-container movie overlay'
             >
                 <img src={`https://image.tmdb.org/t/p/w500/${key?.poster_path}`} className='card-img' alt='...'/>

                 <div className='info'>
                     <h3>{key?.title}</h3>
                     <span>
               {key?.vote_average}
           </span>
                 </div>
                 <div className='over'>
                     <p>{key?.overview}</p>
                 </div>

             </div>
         )
     }
 }

 export default AddFavourites
