import React, {useEffect, useState} from "react";
import {BrowserRouter, Route, Redirect, Switch} from "react-router-dom";
import Header from "./components/Header/header";
import Card from "./components/Card/card";
import MoviesDetail from './components/MoviesDetail/detail'


const API_KEY = "8bd7ad481d1640e1826b9b2df2461c0f"



export default function App() {
    const [search, setSearch] = useState("");
    const [movies, setMovies] = useState([]);

    const PRO_API = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&language=en-US&page=1&include_adult=false&query=3`;
    const SEARCH_API = `https://api.themoviedb.org/3/search/movie?api_key=${API_KEY}&query=/`;

    useEffect(() =>{
        fetch(PRO_API)
            .then(response => response.json())
            .then(info => {
                setMovies(info.result)
            })
    },[])
const Clicker = event => {
        event.preventDefault();

        fetch(SEARCH_API + search)
        .then(rsp => rsp.json())
            .then(data => setMovies(data.results));
}
return (
    <BrowserRouter>
        <Header />
        <Switch>
            <Route exact path="/">
                <div className="container mt-5 mb-5">
                    <form>
                        <div className="row">
                            <div className="col-md-4 offset-md-4">
                                <div class="form-floating mb-3">
                                    <input
                                        type="text"
                                        placeholder="Enter the Movie"
                                        name="search"
                                        value={search}
                                        onChange={event => setSearch(event.target.value)}
                                    />
                                    <label form="floatingInput">Movie Name</label>
                                    <button
                                        onClick={Clicker}
                                        type="submit"
                                    >
                                    </button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
                <div >
                    <div >
                        {movies
                            .filter(item => item.poster_path)
                            .map(item => (
                                <div >
                                    <Card key={item.id} movie={item} />
                                </div>
                            ))}
                    </div>
                </div>
            </Route>
            <Route path="/movie/:movieID">
                <MoviesDetail movies={movies} />
            </Route>
        </Switch>
    </BrowserRouter>
)

}
