import React, {useState} from "react";
import {Link} from "react-router-dom";
const IMG = 'https://image.tmdb.org/t/p/w300/'
 const AddFavourites = () =>{
let keys = JSON.parse(localStorage.getItem('films'))

    console.log(keys)






     return(
         <div className='image-container movie overlay'>
             <img src={`https://image.tmdb.org/t/p/w500/${keys?.poster_path}`} className='card-img' alt='...'/>

             <div className='info'>
                 <h3>{keys?.title}</h3>
                 <span>
               {keys?.vote_average}
           </span>
             </div>
             <div className='over'>
                 <p>{keys?.overview}</p>
             </div>

         </div>
     )
 }

 export default AddFavourites
