import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";


export default function MoviesDetail(props) {
    const {movies} = props;
    const [movie, setMovie] = useState([]);
    const params = useParams();
    console.log(params)

    useEffect(() => {
        setMovie(
            movies.filter(item => Number(item.id) === Number(params.movieID))
       [0] )
    }, [])
    return (
      <div className='container'>
          <nav aria-label='breadcrumb' className='nav'>
              <ol className='breadcrumb'>
                  <li className='breadcrumb-item'><Link to='/'>Home</Link></li>
                  <li className='breadcrumb-item active' aria-current='page'></li>
              </ol>
          </nav>
          <div>
              <img src={`https://image.tmdb.org/t/p/w300/${movie.poster_path}`} className='card-img' alt='...'/>
          <h1>{movie.title}</h1>
              <p>{movie.overview}</p>
              <p>{movie.vote_average}</p>
          </div>
      </div>
    )
}
