import React, {useEffect, useState} from "react";
import {Link, useParams} from "react-router-dom";


const API_KEY = '8bd7ad481d1640e1826b9b2df2461c0f'

function MoviesDetail(props) {
    const [movie, setMovie] = useState([])
    const params = useParams();

    const  MOVIE_API = `https://api.themoviedb.org/3/movie/${params.movieId}?api_key=${API_KEY}&language=en-US`

    useEffect(() =>{
        const axios = require('axios').default
        axios.get(MOVIE_API).then(response => {
            setMovie(response.data)
        })
    }, [params.movieId])

    return(
        <div className='container'>
            <nav className='detail-card'>
             <Link to='/'>
                 Home
             </Link>
            </nav>
            <div>
                <img src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} className='card-img' />
                <div className='container-title'>
                    <h1 className='title'>{movie.title}</h1>
                    <p>{movie.overview}</p>
                    <p>Runtime: {movie.runtime} Minutes</p>
                    <div className='release'>
                        <p>IMDB RATING: {movie.vote_average}</p>
                        <p>{movie.release_data}</p>
                    </div>
                </div>
            </div>
        </div>
    )




}

export default MoviesDetail
