        //         .then(res => {
        //             setTotalPages(res.data);
        //            console.log(res.data)
        //
        //             setLoading(false)
        //         })
        // // }
        // // getFilmList()
        getFilmList()
    },[page] )


     const clickOnDetail = () => {
         console.log('sdfdsf'
         )
         setPage(page + 1)
         console.log(totalPages)
         onClickDetail()
     }
    return (
        <div className='page-container'>
            {totalPages !== page &&
                <button
                    className="link"
                    onClick={() => clickOnDetail()}>
                    {loading ? 'Loading...' : 'Load More'}
                </button>
            }
        </div>
    )
}

export default Pages
